// 2. State Management
const state = {
    theme: localStorage.getItem('theme') || 'light',
    lang: localStorage.getItem('lang') || 'ar'
};

function applyInitialState() {
    // ØªØ·Ø¨ÙŠÙ‚ Ø§Ù„ÙˆØ¶Ø¹ Ø§Ù„Ø¯Ø§ÙƒÙ†
    if (state.theme === 'dark') {
        body.classList.add('dark-mode');
        document.getElementById('themeToggle').innerHTML = '<i class="fas fa-sun"></i>';
    }
    
    // ØªØ·Ø¨ÙŠÙ‚ Ø§Ù„Ù„ØºØ©
    body.classList.remove('ar', 'en');
    body.classList.add(state.lang);
    document.documentElement.setAttribute('dir', state.lang === 'ar' ? 'rtl' : 'ltr');
    document.documentElement.setAttribute('lang', state.lang);
    
    // ØªØ­Ø¯ÙŠØ« Ø§Ù„Ù†ØµÙˆØµ Ø§Ù„Ù…Ø±Ø¦ÙŠØ©
    updateTextVisibility();
}

function updateTextVisibility() {
    const arTexts = document.querySelectorAll('.ar-text');
    const enTexts = document.querySelectorAll('.en-text');
    
    if (state.lang === 'ar') {
        arTexts.forEach(text => {
            text.style.display = 'inline';
        });
        enTexts.forEach(text => {
            text.style.display = 'none';
        });
    } else {
        arTexts.forEach(text => {
            text.style.display = 'none';
        });
        enTexts.forEach(text => {
            text.style.display = 'inline';
        });
    }
}

