// 13. Toast Notification
function showToast(type, message) {
    const container = document.getElementById('toastContainer');
    if (!container) return;
    
    const toast = document.createElement('div');
    toast.className = 'toast';
    toast.style.borderRightColor = type === 'success' ? '#10b981' : '#ef4444';
    toast.innerHTML = `
        <div class="toast-icon">
            <i class="fas ${type === 'success' ? 'fa-check-circle' : 'fa-exclamation-circle'}" 
               style="color:${type === 'success' ? '#10b981' : '#ef4444'}"></i>
        </div>
        <div class="toast-content">
            <h4 class="toast-title">${type === 'success' ? (state.lang === 'ar' ? 'Ù†Ø¬Ø§Ø­! ğŸ‰' : 'Success! ğŸ‰') : (state.lang === 'ar' ? 'Ø®Ø·Ø£! âš ï¸' : 'Error! âš ï¸')}</h4>
            <p class="toast-message">${message}</p>
        </div>
        <button class="toast-close" onclick="this.parentElement.remove()">
            <i class="fas fa-times"></i>
        </button>
    `;
    container.appendChild(toast);
    setTimeout(() => {
        toast.style.opacity = '0';
        setTimeout(() => {
            if (toast.parentNode === container) {
                container.removeChild(toast);
            }
        }, 300);
    }, 5000);
}

